// launcher.js
;(function(){
  const LS_KEY = 'miniOSConfig';
  let desk, startMenu;

  // Load/save persistent settings
  function loadConfig() {
    try { return JSON.parse(localStorage.getItem(LS_KEY)) || {}; }
    catch { return {}; }
  }
  function saveConfig(cfg) {
    localStorage.setItem(LS_KEY, JSON.stringify(cfg));
  }

  // On page load
  window.addEventListener('DOMContentLoaded', ()=>{
    const cfg = loadConfig();
    injectStyles(cfg);
    if (cfg.customCssUrl) loadCustomCss(cfg.customCssUrl);
    if (cfg.customScripts) loadCustomScripts(cfg.customScripts);
    showBootScreen(1500, ()=>{
      if (/\?configure=true\b/.test(location.search)) runConfigurator(cfg);
      else if (!cfg.user) runOOBE(cfg);
      else showLogin(cfg);
    });
  });

  // Boot screen
  function showBootScreen(ms, cb) {
    const cfg = loadConfig();
    const overlay = document.createElement('div');
    Object.assign(overlay.style, {
      position:'fixed', top:0, left:0, right:0, bottom:0,
      background:'#000', display:'flex',
      alignItems:'center', justifyContent:'center', zIndex:9999
    });
    const img = document.createElement('img');
    img.src = cfg.bootImage || 'data:image/png;base64,';
    img.style.maxWidth='100%'; img.style.maxHeight='100%';
    overlay.appendChild(img);
    document.body.appendChild(overlay);
    setTimeout(()=>{
      document.body.removeChild(overlay);
      cb();
    }, ms);
  }

  // Load external CSS
  function loadCustomCss(url) {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = url;
    document.head.appendChild(link);
  }

  // Load custom scripts
  function loadCustomScripts(scripts) {
    scripts.forEach(src => {
      const s = document.createElement('script');
      s.src = src;
      document.body.appendChild(s);
    });
  }

  // --- Configurator ---
  function runConfigurator(cfg){
    injectConfiguratorStyles(cfg);
    document.body.innerHTML = '';
    document.title = 'Configurator – ' + (cfg.osName||'MiniOS');

    const ov = document.createElement('div');
    ov.id = 'configurator';
    ov.innerHTML = `
      <div class="cfg-box">
        <h1>MiniOS Configurator</h1>
        <div class="cfg-section">
          <h2>Custom CSS URL</h2>
          <input id="cfg_css" placeholder="https://example.com/style.css" value="${cfg.customCssUrl||''}">
        </div>
        <div class="cfg-section">
          <h2>Custom Script URLs (one per line)</h2>
          <textarea id="cfg_scripts" rows="4" placeholder="https://example.com/script.js">${(cfg.customScripts||[]).join('\n')}</textarea>
        </div>
        <div class="url-load">
          <input id="cfg_json_url" placeholder="https://example.com/apps.json">
          <button id="cfg_load_json">Load Apps JSON</button>
        </div>
        <div class="cfg-section">
          <h2>General</h2>
          <label>OS Name<input id="cfg_name" value="${cfg.osName||'MiniOS'}"></label>
          <label>Accent Color<input id="cfg_accent" type="color" value="${cfg.accentColor||'#1f2937'}"></label>
          <label>Background Color<input id="cfg_bg" type="color" value="${cfg.bgColor||'#ffffff'}"></label>
          <label>Background Image URL<input id="cfg_bgi" value="${cfg.bgImage||''}"></label>
          <label>Boot Image URL<input id="cfg_boot" value="${cfg.bootImage||'https://mini.com/os/bs.png'}"></label>
        </div>
        <div class="cfg-section">
          <h2>Security</h2>
          <label>Username<input id="cfg_user" value="${cfg.user||''}"></label>
          <label>Password<input id="cfg_pass" type="password" value="${cfg.pass||''}"></label>
          <label>Min Password Length<input id="cfg_minlen" type="number" min="1" value="${cfg.minPassLen||6}"></label>
        </div>
        <div class="cfg-section">
          <h2>Apps</h2>
          <div id="cfg_app_list"></div>
          <button id="cfg_add_app">Add App</button>
        </div>
        <div class="cfg-footer">
          <button id="cfg_export">Export Config</button>
          <input type="file" id="cfg_import" accept=".osfig">
          <button id="cfg_save">Save & Exit</button>
          <p class="note">Reload without <code>?configure=true</code> to apply.</p>
        </div>
        <p id="cfg_msg" class="msg"></p>
      </div>`;
    document.body.appendChild(ov);

    // Populate apps list
    const appList = ov.querySelector('#cfg_app_list');
    function addAppRow(app={name:'',icon:'',url:''}) {
      const row = document.createElement('div');
      row.className='app-row';
      row.innerHTML=`
        <input class="app-name" placeholder="Name" value="${app.name}">
        <input class="app-icon" placeholder="Icon URL" value="${app.icon}">
        <input class="app-url" placeholder="URL" value="${app.url}">
        <button class="app-remove">✕</button>`;
      row.querySelector('.app-remove').onclick = ()=> row.remove();
      appList.appendChild(row);
    }
    (cfg.apps||[]).forEach(addAppRow);
    ov.querySelector('#cfg_add_app').onclick = ()=> addAppRow();

    // Load external apps.json
    ov.querySelector('#cfg_load_json').onclick = async ()=>{
      const url = ov.querySelector('#cfg_json_url').value.trim();
      if (!url) return alert('Enter JSON URL');
      try {
        const data = await fetch(url).then(r=>r.json());
        cfg.apps = data;
        appList.innerHTML = '';
        data.forEach(addAppRow);
      } catch {
        alert('Failed to load JSON');
      }
    };

    // Export config
    ov.querySelector('#cfg_export').onclick = ()=>{
      const data = JSON.stringify(cfg, null, 2);
      const blob = new Blob([data], {type:'application/json'});
      const a = document.createElement('a');
      a.href = URL.createObjectURL(blob);
      a.download = 'settings.osfig';
      a.click();
    };

    // Import config
    ov.querySelector('#cfg_import').onchange = e => {
      const file = e.target.files[0];
      if (!file) return;
      const reader = new FileReader();
      reader.onload = () => {
        try {
          const imported = JSON.parse(reader.result);
          Object.assign(cfg, imported);
          runConfigurator(cfg);
        } catch {
          alert('Invalid file');
        }
      };
      reader.readAsText(file);
    };

    // Save
    ov.querySelector('#cfg_save').onclick = ()=>{
      const msg    = ov.querySelector('#cfg_msg');
      const name   = ov.querySelector('#cfg_name').value.trim() || 'MiniOS';
      const accent = ov.querySelector('#cfg_accent').value;
      const bg     = ov.querySelector('#cfg_bg').value;
      const bgi    = ov.querySelector('#cfg_bgi').value.trim();
      const boot   = ov.querySelector('#cfg_boot').value.trim();
      const user   = ov.querySelector('#cfg_user').value.trim();
      const pass   = ov.querySelector('#cfg_pass').value;
      const minLen = parseInt(ov.querySelector('#cfg_minlen').value,10)||6;
      const cssUrl = ov.querySelector('#cfg_css').value.trim();
      const scripts = ov.querySelector('#cfg_scripts').value
        .split('\n').map(s=>s.trim()).filter(s=>s);
      if (pass.length < minLen) {
        msg.textContent = `Password must be ≥ ${minLen}`;
        msg.classList.add('error');
        return;
      }
      const apps = [];
      ov.querySelectorAll('.app-row').forEach(r=>{
        const nm = r.querySelector('.app-name').value.trim();
        const ic = r.querySelector('.app-icon').value.trim();
        const url= r.querySelector('.app-url').value.trim();
        if(nm&&url) apps.push({name:nm,icon:ic,url});
      });
      Object.assign(cfg,{
        customCssUrl: cssUrl,
        customScripts: scripts,
        osName:name,accentColor:accent,
        bgColor:bg,bgImage:bgi,bootImage:boot,
        user,pass,minPassLen:minLen,
        apps,installed:cfg.installed||[]
      });
      saveConfig(cfg);
      msg.textContent='Configuration saved!';
      msg.classList.remove('error');
      msg.classList.add('success');
    };
  }

  // Configurator styles (unchanged)
  function injectConfiguratorStyles(cfg){
    const s=document.createElement('style');
    s.textContent=`
      html,body{height:100%;margin:0;overflow:auto;font-family:Arial,sans-serif;}
      #configurator{position:fixed;top:0;left:0;right:0;bottom:0;
        background:rgba(240,240,240,0.95);padding:20px;overflow:auto;}
      .cfg-box{max-width:700px;margin:0 auto;background:#fff;
        padding:20px;border-radius:8px;box-shadow:0 2px 8px rgba(0,0,0,0.1);}
      .cfg-section{margin-top:24px;}
      h1{color:${cfg.accentColor};margin-bottom:16px;}
      h2{color:#333;margin-bottom:8px;}
      input,textarea{width:100%;padding:6px;border:1px solid #ccc;border-radius:4px;margin:4px 0;}
      textarea{resize:vertical;}
      .url-load{display:flex;gap:8px;margin-bottom:16px;}
      .url-load button{padding:6px 12px;background:${cfg.accentColor};
        color:#fff;border:none;border-radius:4px;cursor:pointer;}
      #cfg_app_list{max-height:200px;overflow-y:auto;border:1px solid #ddd;
        padding:8px;border-radius:4px;}
      .app-row{display:flex;gap:8px;margin:4px 0;}
      .app-remove{background:#e74c3c;color:#fff;border:none;
        border-radius:4px;padding:6px 8px;cursor:pointer;}
      .cfg-footer{display:flex;align-items:center;gap:8px;margin-top:16px;}
      .cfg-footer button{padding:8px 12px;background:${cfg.accentColor};
        color:#fff;border:none;border-radius:4px;cursor:pointer;}
      .note{font-size:.85rem;color:#777;}
      .msg{margin-top:12px;font-weight:bold;}
      .msg.error{color:#c00;} .msg.success{color:#080;}
    `;
    document.head.appendChild(s);
  }

  // --- First-run / Login ---
  function runOOBE(cfg){ showFirstRun(cfg) }
  function showFirstRun(cfg){
    const overlay = document.createElement('div');
    Object.assign(overlay.style,{
      position:'fixed',top:0,left:0,right:0,bottom:0,
      background:'#fff',display:'flex',alignItems:'center',
      justifyContent:'center',zIndex:9998
    });
    const box = document.createElement('div');
    Object.assign(box.style,{
      background:'#f9f9f9',padding:'24px',
      borderRadius:'8px',width:'320px',
      boxShadow:'0 2px 8px rgba(0,0,0,0.1)'
    });
    box.innerHTML=`
      <h2>Welcome to ${cfg.osName||'MiniOS'}</h2>
      <input id="o_u" placeholder="Username" style="width:100%;margin:8px 0;padding:8px;"><br>
      <input id="o_p" type="password" placeholder="Password" style="width:100%;margin:8px 0;padding:8px;"><br>
      <p id="o_msg" style="color:#c00;height:1em"></p>
      <button id="o_n" style="width:100%;padding:10px;background:${cfg.accentColor};
        color:#fff;border:none;border-radius:4px;cursor:pointer">Next</button>`;
    overlay.appendChild(box); document.body.appendChild(overlay);
    box.querySelector('#o_n').onclick=()=>{
      const u=box.querySelector('#o_u').value.trim(),
            p=box.querySelector('#o_p').value,
            m=box.querySelector('#o_msg');
      if(!u){m.textContent='Enter username';return;}
      if(p.length<6){m.textContent='Password too short';return;}
      cfg.user=u;cfg.pass=p;saveConfig(cfg);
      overlay.remove(); buildDesktop(cfg);
    };
  }
  function showLogin(cfg){
    const overlay = document.createElement('div');
    Object.assign(overlay.style,{
      position:'fixed',top:0,left:0,right:0,bottom:0,
      background:'rgba(245,245,245,0.95)',display:'flex',
      alignItems:'center',justifyContent:'center',zIndex:9997
    });
    const box = document.createElement('div');
    Object.assign(box.style,{
      background:'#fff',padding:'24px',
      borderRadius:'8px',width:'300px',
      boxShadow:'0 2px 8px rgba(0,0,0,0.1)'
    });
    box.innerHTML=`
      <h2>${cfg.osName||'MiniOS'}</h2>
      <input id="li_u" placeholder="Username" style="width:100%;margin:8px 0;padding:8px;"><br>
      <input id="li_p" type="password" placeholder="Password" style="width:100%;margin:8px 0;padding:8px;"><br>
      <p id="li_e" style="color:#c00;height:1em"></p>
      <button id="li_b" style="width:100%;padding:10px;background:${cfg.accentColor};
        color:#fff;border:none;border-radius:4px;cursor:pointer">Log In</button>`;
    overlay.appendChild(box); document.body.appendChild(overlay);
    box.querySelector('#li_b').onclick=()=>{
      const u=box.querySelector('#li_u').value,
            p=box.querySelector('#li_p').value,
            e=box.querySelector('#li_e');
      if(u===cfg.user&&p===cfg.pass){
        overlay.remove(); buildDesktop(cfg);
      } else {
        e.textContent='Invalid credentials';
      }
    };
  }

  // Global styles & build desktop
  function injectStyles(cfg){
    const css=`*{box-sizing:border-box;margin:0;padding:0}
      body{overflow:hidden;background:#333}
      #desktop{position:absolute;top:0;left:0;right:0;bottom:60px;
        display:flex;flex-wrap:wrap;align-content:flex-start;padding:20px;
        background:${cfg.bgColor||'#222'} url(${cfg.bgImage||''}) center/cover no-repeat}
      .icon{width:80px;height:80px;margin:8px;display:flex;
        flex-direction:column;align-items:center;justify-content:flex-start;
        background:rgba(255,255,255,0.1);border-radius:8px;cursor:pointer;transition:.2s}
      .icon:hover{background:rgba(255,255,255,0.2)}
      .icon img{max-width:60%;max-height:60%;margin-top:8px}
      .icon-label{margin-top:6px;font-size:.75rem;color:#fff;text-align:center;width:100%}
      #taskbar{position:fixed;bottom:0;left:0;right:0;height:60px;
        display:flex;align-items:center;padding:0 20px;
        background:rgba(0,0,0,0.8);box-shadow:0 -2px 6px rgba(0,0,0,0.5)}
      #start-btn{padding:6px 12px;margin-right:12px;background:${cfg.accentColor};
        color:#fff;border:none;border-radius:4px;cursor:pointer}
      .tb-item{margin-right:12px;padding:6px 12px;background:rgba(255,255,255,0.1);
        color:#fff;border-radius:4px;cursor:pointer}
      .tb-item:hover{background:rgba(255,255,255,0.2)}
      .tb-time{margin-left:auto;font-size:.9rem;color:#fff}
      #start-menu{position:fixed;bottom:60px;left:20px;
        background:#fff;border-radius:4px;box-shadow:0 2px 8px rgba(0,0,0,0.3);
        display:none;flex-direction:column;width:200px}
      #start-menu .sm-item{padding:10px 12px;color:#333;cursor:pointer;border-bottom:1px solid #eee}
      #start-menu .sm-item:hover{background:#f0f0f0}
      .window{position:absolute;top:60px;left:60px;width:640px;height:480px;
        background:#fff;border-radius:4px;box-shadow:0 2px 8px rgba(0,0,0,0.3);
        display:flex;flex-direction:column;resize:both;overflow:hidden;z-index:1000}
      .win-header{background:${cfg.accentColor};color:#fff;padding:8px;
        display:flex;justify-content:space-between;align-items:center;cursor:move}
      .win-close{cursor:pointer}
      .win-content{flex:1;overflow:auto;background:#fafafa;padding:12px}
      .win-content button{padding:6px 12px;margin:4px 0;
        background:${cfg.accentColor};color:#fff;border:none;border-radius:4px;cursor:pointer;width:100%}
      .win-content input,textarea{width:100%;margin:4px 0;padding:6px;border:1px solid #ccc;border-radius:4px}`;
    const s=document.createElement('style'); s.textContent=css; document.head.appendChild(s);
  }

  function buildDesktop(cfg){
    if(!desk){
      desk=document.createElement('div'); desk.id='desktop';
      document.body.appendChild(desk);
      // start menu
      startMenu=document.createElement('div'); startMenu.id='start-menu';
      ['App Manager','Settings','Power Off'].forEach(text=>{
        const itm=document.createElement('div');
        itm.className='sm-item'; itm.textContent=text;
        itm.onclick=()=>{
          startMenu.style.display='none';
          if(text==='App Manager') openAppManager(cfg,buildDesktop);
          if(text==='Settings') location.search='?configure=true';
          if(text==='Power Off') showPowerOff();
        };
        startMenu.appendChild(itm);
      });
      document.body.appendChild(startMenu);
    } else {
      desk.innerHTML='';
    }

    let tb=document.getElementById('taskbar'); if(tb) tb.remove();
    tb=document.createElement('div'); tb.id='taskbar'; document.body.appendChild(tb);

    const startBtn=document.createElement('button');
    startBtn.id='start-btn'; startBtn.textContent='Start';
    startBtn.onclick=()=> startMenu.style.display=startMenu.style.display==='flex'?'none':'flex';
    tb.appendChild(startBtn);

    const amBtn=document.createElement('div');
    amBtn.className='tb-item'; amBtn.textContent='App Manager';
    amBtn.onclick=()=> openAppManager(cfg,buildDesktop);
    tb.appendChild(amBtn);

    const timeDiv=document.createElement('div');
    timeDiv.className='tb-item tb-time';
    timeDiv.textContent=new Date().toLocaleTimeString();
    setInterval(()=> timeDiv.textContent=new Date().toLocaleTimeString(),500);
    tb.appendChild(timeDiv);

    (cfg.installed||[]).forEach(i=>{
      const app=cfg.apps[i];
      renderIcon(app,desk,()=>openAppIframe(app));
    });
  }

  // Power Off UI
  function showPowerOff(){
    const win=document.createElement('div'); win.className='window';
    win.innerHTML=`
      <div class="win-header"><span>Powered Off</span><span class="win-close">X</span></div>
      <div class="win-content" style="display:flex;flex-direction:column;align-items:center;justify-content:center;">
        <p>System is off.</p><button id="reboot_btn">Reboot</button>
      </div>`;
    document.body.appendChild(win);
    win.querySelector('.win-close').onclick=()=> win.remove();
    win.querySelector('#reboot_btn').onclick=()=> location.reload();
    makeDraggable(win);
  }

  // Render desktop icon
  function renderIcon(app, container, onClick){
    const ico=document.createElement('div'); ico.className='icon';
    if(app.icon){
      const img=document.createElement('img');
      img.src=app.icon;
      ico.appendChild(img);
    }
    const lbl=document.createElement('div');
    lbl.className='icon-label';
    lbl.textContent=app.name;
    ico.appendChild(lbl);
    ico.onclick=onClick;
    container.appendChild(ico);
  }

  // Open app in window
  function openAppIframe(app){
    const win=document.createElement('div'); win.className='window';
    win.innerHTML=`
      <div class="win-header"><span>${app.name}</span><span class="win-close">X</span></div>
      <div class="win-content"><iframe src="${app.url}" style="width:100%;height:100%;border:none"></iframe></div>`;
    document.body.appendChild(win);
    win.querySelector('.win-close').onclick=()=> win.remove();
    makeDraggable(win);
  }

  // App Manager
  function openAppManager(cfg, onChange){
    const win=document.createElement('div'); win.className='window';
    win.innerHTML=`
      <div class="win-header"><span>App Manager</span><span class="win-close">X</span></div>
      <div class="win-content"><h3>Available Apps</h3><ul id="am-list"></ul></div>`;
    document.body.appendChild(win);
    win.querySelector('.win-close').onclick=()=> win.remove();
    makeDraggable(win);

    const list=win.querySelector('#am-list');
    list.style.listStyle='none'; list.style.padding=0;
    cfg.apps.forEach((app,i)=>{
      const li=document.createElement('li');
      li.style.cssText='display:flex;align-items:center;margin:8px 0;';
      const span=document.createElement('span');
      span.style.flex='1'; span.textContent=app.name;
      const btn=document.createElement('button');
      const installed=(cfg.installed||[]).includes(i);
      btn.textContent=installed?'Uninstall':'Install';
      btn.onclick=()=>{
        cfg.installed=cfg.installed||[];
        if(installed) cfg.installed=cfg.installed.filter(x=>x!==i);
        else cfg.installed.push(i);
        saveConfig(cfg);
        win.remove();
        onChange(cfg);
        openAppManager(cfg,onChange);
      };
      li.appendChild(span);
      li.appendChild(btn);
      list.appendChild(li);
    });
  }

  // First-run OOBE
  function runOOBE(cfg){ showFirstRun(cfg) }
  function showFirstRun(cfg){
    const overlay=document.createElement('div');
    Object.assign(overlay.style,{
      position:'fixed',top:0,left:0,right:0,bottom:0,
      background:'#fff',display:'flex',alignItems:'center',
      justifyContent:'center',zIndex:9998
    });
    const box=document.createElement('div');
    Object.assign(box.style,{
      background:'#f9f9f9',padding:'24px',
      borderRadius:'8px',width:'320px',
      boxShadow:'0 2px 8px rgba(0,0,0,0.1)'
    });
    box.innerHTML=`
      <h2>Welcome to ${cfg.osName||'MiniOS'}</h2>
      <input id="o_u" placeholder="Username" style="width:100%;margin:8px 0;padding:8px;"><br>
      <input id="o_p" type="password" placeholder="Password" style="width:100%;margin:8px 0;padding:8px;"><br>
      <p id="o_msg" style="color:#c00;height:1em"></p>
      <button id="o_n" style="width:100%;padding:10px;background:${cfg.accentColor};
        color:#fff;border:none;border-radius:4px;cursor:pointer">Next</button>`;
    overlay.appendChild(box);
    document.body.appendChild(overlay);
    box.querySelector('#o_n').onclick=()=>{
      const u=box.querySelector('#o_u').value.trim(),
            p=box.querySelector('#o_p').value,
            m=box.querySelector('#o_msg');
      if(!u){ m.textContent='Enter username'; return; }
      if(p.length<6){ m.textContent='Password too short'; return; }
      cfg.user=u; cfg.pass=p; saveConfig(cfg);
      overlay.remove(); buildDesktop(cfg);
    };
  }

  // Login
  function showLogin(cfg){
    const overlay=document.createElement('div');
    Object.assign(overlay.style,{
      position:'fixed',top:0,left:0,right:0,bottom:0,
      background:'rgba(245,245,245,0.95)',display:'flex',
      alignItems:'center',justifyContent:'center',zIndex:9997
    });
    const box=document.createElement('div');
    Object.assign(box.style,{
      background:'#fff',padding:'24px',
      borderRadius:'8px',width:'300px',
      boxShadow:'0 2px 8px rgba(0,0,0,0.1)'
    });
    box.innerHTML=`
      <h2>${cfg.osName||'MiniOS'}</h2>
      <input id="li_u" placeholder="Username" style="width:100%;margin:8px 0;padding:8px;"><br>
      <input id="li_p" type="password" placeholder="Password" style="width:100%;margin:8px 0;padding:8px;"><br>
      <p id="li_e" style="color:#c00;height:1em"></p>
      <button id="li_b" style="width:100%;padding:10px;background:${cfg.accentColor};
        color:#fff;border:none;border-radius:4px;cursor:pointer">Log In</button>`;
    overlay.appendChild(box);
    document.body.appendChild(overlay);
    box.querySelector('#li_b').onclick=()=>{
      const u=box.querySelector('#li_u').value,
            p=box.querySelector('#li_p').value,
            e=box.querySelector('#li_e');
      if(u===cfg.user&&p===cfg.pass){
        overlay.remove(); buildDesktop(cfg);
      } else {
        e.textContent='Invalid credentials';
      }
    };
  }

  // Draggable windows
  function makeDraggable(win){
    const hdr=win.querySelector('.win-header');
    hdr.onmousedown=e=>{
      const dx=e.clientX-win.offsetLeft, dy=e.clientY-win.offsetTop;
      function mv(ev){
        win.style.left=ev.clientX-dx+'px';
        win.style.top=ev.clientY-dy+'px';
      }
      document.addEventListener('mousemove',mv);
      document.addEventListener('mouseup',()=>document.removeEventListener('mousemove',mv),{once:true});
    };
  }

})();
